      error => {
        //TODO
      }
    );
  }


  onEditEvent(data){
    console.log(data)
    this.setState({edit:true, eventEdit: data})
  }

  render() {
    return (
      this.state.event ? (
        <EventSelected event={this.state.event}></EventSelected>
