/**
 * Filename: principal.js
 * Author: kristalduran97@gmail.com
 * Date: 15/12/2019
 * Description: Principal
 */
import React from 'react';
import './styles.scss';
import { Button } from 'semantic-ui-react';
// import { Link } from 'react-router-dom';
import Header from '../../Header/index';
import Footer from '../../Footer/index';
import Menu from '../../Menu/index';
import { thumbsDown } from 'ionicons/icons';

class AddUser extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      edit: this.props.user,
      user: this.props.user || {},
    }
  }

  onChange(e){
    const user = { ... this.state.user, name: e.currentTarget.value};
    this.setState({user});
    e.currentTarget.value = this.state.user;
    console.log(e.currentTarget.value);

  }

  render() {
    return (
      <div className="user">
        <Header/>
        <Menu events={false} users={true}/>
        <div className="user__info">
          {this.state.edit ? 
            (<p className='user__info__titule'>Editar Usuario</p>) :
            (<p className='user__info__titule'>Registrar de Usuario</p>)
          }
          <input className="user__info__input" placeholder='Nombre Completo' value={this.state.user.name || ''} onChange={this.onChange.bind(this)}></input>
          <input className="user__info__input" placeholder='Correo Electr칩nico' value={this.state.user.email || ''}></input>
          <input className="user__info__input" placeholder='Direci칩n' value={this.state.user.address || ''} type='text'></input>
          {this.state.edit ? 
            (<div></div>) :
            (<div className="user__info__password">
              <input className="user__info__input" placeholder='Contrase침a' type="password"></input>
              <input className="user__info__input" placeholder='Confirmar Contrase침a' type="password"></input>
            </div>)
          }
          <div className='user__info__buttons'>
            {this.state.edit ? 
              (<div className='user__info__buttons'>
                <Button className='user__info__buttons__button'>Guardar Cambios</Button>
                <Button className='user__info__buttons__button'>Eliminar Usuario</Button>
              </div>) :
              (<Button className='user__info__buttons__button'>Registrar</Button>)
            }
            <Button href='/getUsers' className='user__info__buttons__button'>Cancelar</Button>
          </div>
        </div>
        <Footer/>
      </div>
    )
  }
}

export default AddUser;
